query GetChildUnits {
  units {
    unit(id: "RN") {
      childUnits(input: {getAll: true, classes: ["SUBSIDIARY"]}) {
        edges {
          node {
            unit {
              id
              name
              properties
              parentUnitIds
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
